mapper.select:
SELECT *
FROM springboot_question
WHERE question LIKE #{key}
ORDER BY date ASC
OFFSET #{offset};

SELECT *
FROM springboot_question
WHERE question LIKE #{key}
ORDER BY date DESC
OFFSET #{offset};

SELECT *
FROM springboot_question
WHERE question LIKE #{key}
ORDER BY views ASC
OFFSET #{offset};

SELECT *
FROM springboot_question
WHERE question LIKE #{key}
ORDER BY views DESC
OFFSET #{offset};

SELECT *
FROM springboot_question
WHERE question LIKE #{key}
ORDER BY answers ASC
OFFSET #{offset};

SELECT *
FROM springboot_question
WHERE question LIKE #{key}
ORDER BY answers DESC
OFFSET #{offset};
